import React, { useState } from 'react';
import { Pagination, Card, CardGroup, Container } from 'react-bootstrap';
import hallOfFame from '../assets/hallOfFame.png'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'
import { observer } from 'mobx-react-lite';
import { fetchHallOfFame } from '../http/halloffameAPI';

const HallOfFame = observer(() => {
const { halloffame } = useContext(Context);

  useEffect(() => {
    fetchHallOfFame(halloffame.page, halloffame.limit).then(data => {
      halloffame.setHallOfFame(data.rows);
      halloffame.setTotalCount(data.count);
    });
  }, [halloffame.page, halloffame.limit]);

  const totalPages = Math.ceil(news.totalCount / news.limit);

  const handlePageChange = (pageNumber) => {
    halloffame.setPage(pageNumber);
  };

  const itemsPerRow = 4; 
  const rows = [];
  for (let i = 0; i < halloffame.hof.length; i += itemsPerRow) {
    const rowItems = halloffame.hof.slice(i, i + itemsPerRow);
    rows.push(rowItems);
  }

  return (
    <div>
    <Container className='block-Standart mt-3' style={{overflowX:'hidden'}}>
    <Pagination style={{marginRight:'20px', marginTop:'100px'}}>
          <Pagination.Prev 
            disabled={halloffame.page === 1} 
            onClick={() => handlePageChange(halloffame.page - 1)} />
          </Pagination>
    {rows.map((row, index) => (
      <Row  key={index} xs = {2} md={4} lg={4} сlassName="mt-5 justify-content-center">
        {row.map(item => (
          <Col key={item.id} className='g-3'>
            <Card className='card-Administration' style={{border:'none'}}>
              <Card.Img variant="top" src={process.env.REACT_APP_API_URL + item.image}  style={{height:'100%', maxWidth:'100%', marginBottom:'-7px'}} />
              <Card.Body>
                <Card.Title 
                  className='body-Semibold' 
                  style={{color:'white',  marginBottom:'4px'}}> 
                  {item.name} 
                </Card.Title>
                <Card.Text 
                  className='body-Small-Text' 
                  style={{color:'white', marginBottom:'4px'}}>
                    {item.post}                  
                </Card.Text>
                <div className='div-Card-Administration block-Standart'/>
                <Card.Text className='body-Smallest-Text mt-2' style={{color:'white'}}>
                    {item.text}            
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
  ))}
  <Pagination style={{marginLeft:'20px', marginTop:'100px'}}>
         <Pagination.Next 
            disabled={halloffame.page === totalPages} 
            onClick={() => handlePageChange(halloffame.page + 1)} />
        </Pagination>
      </Container>
      <Pagination className='mt-5 block-Standart'>
          <div className='block-Standart'>
            {pages.map(page =>
                <Pagination.Item
                    key={page}
                    active={halloffame.page === page}
                    onClick={() => halloffame.setPage(page)}
                >
                    {page}
                </Pagination.Item>
            )}
             </div>
      </Pagination>
      
    </div>
  );
})

export default HallOfFame;
