import {$authHost, $host} from "./index";
import jwt_decode from "jwt-decode";

export const createDocument = async (document) => {
    const {data} = await $authHost.post('api/documents', document)
    return data
}

export const fetchDocument = async (page, limit=3) => {
    const {data} = await $host.get('api/documents', {params: {
        page, limit
    }})
    return data
}

export const fetchOneDocument = async (id) => {
    const {data} = await $host.get('api/documents/' + id)
    return data
}
