import React, { useState } from 'react';
import { Pagination, Card, CardGroup, Container } from 'react-bootstrap';
import Building from '../assets/Building.png'
import Docs from '../assets/Docs.svg'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'

let active = 1
const itemsPerPage = 3; // Количество карточек на странице
const totalItems = 15; // Общее количество карточек

function Documents() {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // Функция для генерации массива карточек на основе текущей страницы
  const getCurrentPageItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return Array.from({ length: totalItems }, (_, index) => index).slice(startIndex, endIndex);
  };

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Функция для генерации элементов пагинации
  const renderPaginationItems = () => {
    const items = [];
    for (let number = 1; number <= totalPages; number++) {
      items.push(
        <Pagination.Item style={{
          width: '30px',
          height: '30px',
          borderRadius: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          margin: '0 5px',
          backgroundColor: active === number ? 'blue' : 'lightgray',
          color: 'white',
        }} key={number} active={number === currentPage} onClick={() => handlePageChange(number)}>
          {number}
        </Pagination.Item>
      );
    }
    return items;
  };

  return (
    <div>
    <Container className='block-Standart'>
      <Row  xs ={1} md={3} lg={3} сlassName="mt-5 block-Standart">
        {getCurrentPageItems().map((itemIndex) => (
          <Col key={itemIndex} className='g-5'>
            <Card className='card-Docs' style={{display:'flex', border:'none', flexDirection:'row'}}>
              <Card.Img variant='left' src={Docs} alt='Документ' style={{paddingBottom:'20px', paddingTop:'20px'}}/>
              <Card.Text className='body-Bold' style={{color:'white'}}> sdfffffffffff </Card.Text>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination className='block-Standart mt-2'>
        <Pagination.Prev style={{transform:'translateX(-560px) translateY(-140px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
            <div className='block-Standart mt-4'>
            {renderPaginationItems()}
            </div>
        <Pagination.Next style={{transform:'translateX(560px) translateY(-145px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
      </Pagination>
    </div>
  );
}

export default Documents;
