import React, { useState } from 'react';
import { Pagination, Card, Container } from 'react-bootstrap';
import NewsImg from '../assets/NewsImg.png';
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'

const itemsPerPage = 3; // Количество карточек на странице
const totalItems = 12; // Общее количество карточек

function NewsCards() {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // Функция для генерации массива карточек на основе текущей страницы
  const getCurrentPageItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return Array.from({ length: totalItems }, (_, index) => index).slice(startIndex, endIndex);
  };

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
        <Container className='justify-content-center'>
      <Row xs={1} md={2} lg={3} сlassName="mt-5">
        {getCurrentPageItems().map((itemIndex) => (
          <Col key={itemIndex} className='col-3 g-5'>
            <Card className='card-News' style={{backgroundColor:'#F4F5F6', border:'none'}}>
              <Card.Img variant="top" src={NewsImg} style={{marginBottom:'-10px'}} />
              <Card.Body>
                <Card.Title className='body-Regular-Gradient' style={{textAlign:'center', marginBottom:'4px'}}> ДАТА {itemIndex + 1}</Card.Title>
                <div className='div-Card' style={{display:'flex', justifyContent: 'center'}}/>
                <Card.Title className='caption-Gradient' style={{textAlign:'center'}}>Заголовок {itemIndex + 1}</Card.Title>
                <Card.Text className='body-Small-Text'>
                Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.
                Ut enim ad minim veniam, quis nostrud exercitation ullamco laboris nisi ut aliquip ex ea commodo consequat.
                Duis aute irure dolor in reprehenderit in voluptate velit esse cillum dolore eu fugiat nulla pariatur.
                Excepteur sint occaecat cupidatat non proident, sunt in culpa qui officia deserunt mollit anim id est laborum.
                </Card.Text>
                <div style={{justifyContent:'center', display:'flex'}}>
                <Button className='button' style={{border:'none'}}>
                    Подробнее
                </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination className='mt-5 justify-content-center'>
        <Pagination.Prev style={{transform:'translateX(85px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
        <Pagination.Next  style={{transform:'translateX(85px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
        <div style={{transform:'translateX(519px)'}}>
        <Button className='button' style={{border: 'none'}}>
          Больше новостей
        </Button>
        </div>
      </Pagination>
    </div>
  );
}

export default NewsCards;
