import React, { useContext, useState, useEffect } from 'react';
import { Pagination, Card, Container } from 'react-bootstrap';
import administration from '../assets/administration.png'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'
import { observer } from 'mobx-react-lite';
import { Context } from '..';

const Administration = observer(() => {
  const {administrators} = useContext(Context)

  useEffect(() => {
    fetchNews(admin.page, admin.limit).then(data => {
      admin.setNovelty(data.rows);
      admin.setTotalCount(data.count);
    });
  }, [admin.page, admin.limit]);

  const totalPages = Math.ceil(news.totalCount / news.limit);

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  return (
    <div>
    <Container className='block-Standart'>
      <Row  xs ={1} сlassName="mt-5 justify-content-center">
        {admin.admins.map((adminItem,index) => (
          <Col key={itemIndex} className='g-5 col-md-4 col-lg-4'>
            <Card className='card-Administration' style={{border:'none'}}>
              <Card.Img variant="top" src={process.env.REACT_APP_API_URL + adminItem.image} style={{marginBottom:'-10px', maxWidth:'100%'}} />
              <Card.Body>
                <Card.Title className='caption-sm mt-1' style={{textAlign:'center'}}>
                  {adminItem.name}
                </Card.Title>
                    <div className='div-Card-Administration block-Standart'/>
                <Card.Text className='body-Semibold block-Standart' style={{color:'white'}}>
                    {adminItem.text}                   
                </Card.Text>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination className='block-Standart mt-2'>
        <Pagination.Prev style={{transform:'translateX(-370px) translateY(-210px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
            <div className='block-Standart mt-4'>
            {renderPaginationItems()}
            </div>
        <Pagination.Next style={{transform:'translateX(370px) translateY(-210px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
      </Pagination>
    </div>
  );
})

export default Administration;
