import React, { useState } from 'react';
import { Pagination, Card, CardGroup, Container } from 'react-bootstrap';
import NewsImg from '../assets/NewsImg.png';
import PopServ from '../assets/PopServ.png'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'

const itemsPerPage = 3; // Количество карточек на странице
const totalItems = 12; // Общее количество карточек

function PopularServices() {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // Функция для генерации массива карточек на основе текущей страницы
  const getCurrentPageItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return Array.from({ length: totalItems }, (_, index) => index).slice(startIndex, endIndex);
  };

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Функция для генерации элементов пагинации
  const renderPaginationItems = () => {
    const items = [];
    for (let number = 1; number <= totalPages; number++) {
      items.push(
        <Pagination.Item key={number} active={number === currentPage} onClick={() => handlePageChange(number)}>
          {number}
        </Pagination.Item>
      );
    }
    return items;
  };

  return (
    <div>
    <Container className='justify-content-center'>
      <Row  xs ={1} md={3} lg={3} сlassName="mt-5 justify-content-center">
        {getCurrentPageItems().map((itemIndex) => (
          <Col key={itemIndex} className='g-5' style={{}}>
            <Card className='card-Popular-Services' style={{border:'none'}}>
              <Card.Img variant="top" src={PopServ} style={{marginBottom:'-10px'}} />
              <Card.Body>
                <Card.Title className='caption mb-3 mt-3' style={{textAlign:'center', marginBottom:'4px'}}> Название услуги {itemIndex + 1}</Card.Title>
                    <div className='div-Card-2' style={{display:'flex', justifyContent: 'center'}}/>
                <Card.Text className='body-Regular mt-3' style={{color:'white', wordWrap:'break-word'}}>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.                   
                </Card.Text>
                <div style={{display:'flex', justifyContent:'center'}}>
                    <Button className='button' style={{border:'none'}}>
                        Подробнее
                    </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination className='mt-5 justify-content-center'>
        <Pagination.Prev style={{transform:'translateX(85px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
        <Pagination.Next style={{transform:'translateX(85px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
        <div style={{transform:'translateX(645px)'}}>
        <Button className='button' style={{border: 'none'}}>
        Больше новостей
        </Button>
        </div>
      </Pagination>
    </div>
  );
}

export default PopularServices;
