import React, { useEffect, useContext } from 'react';
import { Container, Row, Col, Card, Button, Pagination } from 'react-bootstrap';
import { Context } from '../index';
import { observer } from 'mobx-react-lite';
import { fetchDocument } from '../http/documentsAPI';
import { fetchOneDocument } from '../http/documentsAPI';
import Docs from '../assets/Docs.svg'
import fileDownload from 'js-file-download';

const Documents = observer(() => {
  const { documents } = useContext(Context);
  const pages = []

  useEffect(() => {
    fetchDocument(documents.page, documents.limit).then(data => {
      documents.setDocs(data.rows);
      documents.setTotalCount(data.count);
    });
  }, [documents.page, documents.limit]);

  const totalPages = Math.ceil(documents.totalCount / documents.limit);

  for (let i = 0; i < totalPages; i++) {
    pages.push(i + 1)
  }

  const handlePageChange = (pageNumber) => {
    documents.setPage(pageNumber);
  };

  const downloadDocument = async (id) => {
    try {
        const fileUrl = await fetchOneDocument(id); // Получаем URL файла
        const response = await fetch(fileUrl);
        const blob = await response.blob();

        // Создаем объект URL для файла
        const url = window.URL.createObjectURL(blob);
        const link = document.createElement('a');
        link.href = url;
        link.setAttribute('download', `document_${id}.docx`); // Замените `.docx` на правильный формат файла
        document.body.appendChild(link);
        link.click();
        document.body.removeChild(link);

        // Освобождаем созданный объект URL
        window.URL.revokeObjectURL(url);
    } catch (error) {
        console.error('Ошибка при скачивании файла:', error);
    }
};


  return (
    <div>
    <Container className='block-Standart mt-4'>
    <Pagination style={{marginRight:'20px', marginTop:'50px'}}>
          <Pagination.Prev 
            disabled={documents.page === 1} 
            onClick={() => handlePageChange(documents.page - 1)} />
          </Pagination>
      <Row  xs ={1} md={1} lg={3} сlassName="mt-5 block-Standart">
        {documents.docs.map((docsItem, index) => (
          <Col key={index} className='g-5' style={{width:'25vw'}}>
            <Card className='card-Docs' style={{display:'flex', border:'none'}}>
              <Card.Img 
                variant='left' 
                src={Docs} 
                alt='Документ' 
                style={{paddingBottom:'15px', paddingTop:'15px', paddingLeft:'20px'}}/>
              <Card.Body style={{width:'100%'}}>
                <div className='body-Bold' style={{color:'white', cursor: 'pointer'}}  onClick={() => downloadDocument(docsItem.id)}> {docsItem.name} </div>
                <div className='body-Small-Text' style={{color:'white'}}> {docsItem.text} </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      <Pagination style={{marginLeft:'20px', marginTop:'50px'}}>
         <Pagination.Next 
            disabled={documents.page === totalPages} 
            onClick={() => handlePageChange(documents.page + 1)} />
        </Pagination>
      </Container>
      <Pagination className='mt-5 block-Standart'>
          <div className='block-Standart'>
            {pages.map(page =>
                <Pagination.Item
                    key={page}
                    active={documents.page === page}
                    onClick={() => documents.setPage(page)}
                >
                    {page}
                </Pagination.Item>
            )}
             </div>
      </Pagination>
    </div>
  );
})

export default Documents;
