import React, { useContext, useState } from 'react';
import { observer } from 'mobx-react';
import { Context } from '../index'; // Путь к файлу глобального хранилища
import { Modal, Form, Button } from 'react-bootstrap';
import { createNews } from './api/newsApi'; // Путь к файлу с функциями API

const CreateNews = observer(({ show, onHide }) => {
  const { news } = useContext(Context);
  const [newsHeader, setNewsHeader] = useState('');
  const [newsDescription, setNewsDescription] = useState('');
  const [newsDate, setNewsDate] = useState(0);
  const [newsImg, setNewsImg] = useState(null);

  const selectFile = e => {
    setNewsImg(e.target.files[0]);
  };

  const addNews = () => {
    const formData = new FormData();
    formData.append('name', newsHeader);
    formData.append('text', newsDescription);
    formData.append('date', `${newsDate}`);
    formData.append('image', newsImg);
    createNews(formData).then(data => {
      news.setNews([...news.novelty, data]); // Обновляем список новостей в глобальном хранилище
      onHide();
    });
  };

  return (
    <Modal
      show={show}
      onHide={onHide}
      size="lg"
      centered
    >
      <Modal.Header closeButton>
        <Modal.Title id="contained-modal-title-vcenter">
          Добавить новость
        </Modal.Title>
      </Modal.Header>
      <Modal.Body>
        <Form>
          <Form.Control
            value={newsHeader}
            onChange={e => setNewsHeader(e.target.value)}
            className="mt-3"
            placeholder="Введите название новости"
          />
          <Form.Control
            value={newsDescription}
            onChange={e => setNewsDescription(e.target.value)}
            className="mt-3"
            placeholder="Введите текст новости"
          />
          <Form.Control
            value={newsDate}
            onChange={e => setNewsDate(Number(e.target.value))}
            className="mt-3"
            placeholder="Введите дату"
            type="number"
          />
          <Form.Control
            onChange={selectFile}
            className="mt-3"
            placeholder="Выберите файл"
            type="file"
          />
        </Form>
      </Modal.Body>
      <Modal.Footer>
        <Button variant={"outline-danger"} onClick={onHide}>Закрыть</Button>
        <Button variant={"outline-success"} onClick={addNews}>Добавить</Button>
      </Modal.Footer>
    </Modal>
  );
});

export default CreateNews;
