import React, { useContext, useEffect, useState } from 'react';
import ReactDOM from 'react-dom/client';
import { BrowserRouter } from 'react-router-dom';
import AppRouter from './components/AppRouter';
import './fonts/font.css';
import { observer } from 'mobx-react-lite';
import { Context } from '.';
import { check } from './http/userAPI';
import { Spinner } from 'react-bootstrap';

const App = observer(() => {
  const {user} = useContext(Context)
  const [loading, setLoading] = useState(true)
  const [data, setData] = useState(null);
  const [ws, setWs] = useState(null); // Состояние для WebSocket-соединения

  useEffect(() => {
    check().then(data => {
        user.setUser(data)
        user.setIsAuth(true)
    }).finally(() => setLoading(false))

    // Установка WebSocket-соединения
    const socket = new WebSocket('ws://localhost:5000');

    socket.onopen = () => {
      console.log('Connected to WebSocket server');
    };

    socket.onmessage = (event) => {
      console.log('Received message from server:', event.data);
      setData(event.data); // Обновление состояния с полученными данными
    };

    socket.onerror = (error) => {
      console.error('WebSocket error observed:', error);
    };

    socket.onclose = () => {
      console.log('Disconnected from WebSocket server');
    };

    // Сохранение соединения в состоянии
    setWs(socket);

    // Закрытие соединения при размонтировании компонента
    return () => {
      socket.close();
    };
  }, []);

  if(loading) {
    return <Spinner animation='grow'/>;
  }

  return (
    <BrowserRouter>
      <AppRouter />
      {/* Отображение полученных данных, если это необходимо */}
      {data && <div>Received data: {data}</div>}
    </BrowserRouter>
  );
});

export default App;
