import React, { useContext, useState } from 'react';
import Modal from "react-bootstrap/Modal";
import {Button, Form, Row, Col} from "react-bootstrap";
import { Context } from '../..';
import { createNews } from '../../http/newsAPI';

const CreateNews = ({show, onHide}) => {
    const {news} = useContext(Context)
    const [newsHeader, setNewsHeader] = useState('')
    const [newsDescription, setNewsDescription] = useState('')
    const [newsDate, setNewsDate] = useState(0)
    const [newsImg, setNewsImg] = useState(null)

    const selectFile = e => {
        console.log(e.target.files)
    }

    const addNews = () => {
        
    }

    return (
        <Modal
            show={show}
            onHide={onHide}
            size="lg"
            centered
        >
        <Modal.Header closeButton>
            <Modal.Title id="contained-modal-title-vcenter">
                Добавить новость
            </Modal.Title>
        </Modal.Header>
        <Modal.Body>
            <Form>
                <Form.Control
                    value={newsHeader}
                    onChange={e => setNewsHeader(e.target.value)}
                    className="mt-3"
                    placeholder="Введите название новости"
                />
                <Form.Control
                    value={newsDescription}
                    onChange={e => setNewsDescription(e.target.value)}
                    className="mt-3"
                    placeholder="Введите текст новости"
                />
                <Form.Control
                    value={newsDate}
                    onChange={e => setNewsDate(e.target.value)}
                    className="mt-3"
                    placeholder="Введите дату"
                    type="number"
                />
                <Form.Control
                    onChange={selectFile}
                    className="mt-3"
                    placeholder="Выберите файл"
                    type="file"
                />
            </Form>
        </Modal.Body>
        <Modal.Footer>
            <Button variant={"outline-danger"} onClick={onHide}>Закрыть</Button>
            <Button variant={"outline-success"} onClick={onHide}>Добавить</Button>
        </Modal.Footer>
        </Modal>
        );
}

export default CreateNews
