import React, { useState, useEffect } from 'react';
import { Modal, Button } from 'react-bootstrap';
import { getAllNews, deleteNews } from '../../http/newsAPI';
import EditNewsModal from './EditNewsModal'; // Импорт нового компонента

const NewsList = ({ show, onHide }) => {
  const [newsList, setNewsList] = useState([]);
  const [selectedNews, setSelectedNews] = useState(null);
  const [showEditModal, setShowEditModal] = useState(false);

  useEffect(() => {
    if (show) {
      getAllNews().then(setNewsList);
    }
  }, [show]);

  const handleDelete = (id) => {
    deleteNews(id).then(() => {
      setNewsList(newsList.filter((news) => news.id !== id));
    });
  };

  const handleEdit = (news) => {
    setSelectedNews(news);
    setShowEditModal(true);
  };

  const handleCloseEditModal = () => {
    setShowEditModal(false);
  };

  return (
    <Modal show={show} onHide={onHide} size="lg" centered>
      <Modal.Header closeButton>
        <Modal.Title>Список новостей</Modal.Title>
      </Modal.Header>
      <Modal.Body>
        {newsList.map((news) => (
          <div key={news.id}>
            <h2>{news.name}</h2>
            <Button variant="danger" onClick={() => handleDelete(news.id)}>
              Удалить
            </Button>
            <Button variant="primary" onClick={() => handleEdit(news)}>
              Редактировать
            </Button>
          </div>
        ))}
      </Modal.Body>
      <Modal.Footer>
        <Button variant="secondary" onClick={onHide}>
          Закрыть
        </Button>
      </Modal.Footer>
      <EditNewsModal
        show={showEditModal}
        onHide={handleCloseEditModal}
        news={selectedNews}
      />
    </Modal>
  );
};

export default NewsList;
