import React, { useState } from 'react';
import { Pagination, Card, CardGroup, Container } from 'react-bootstrap';
import NewsImg from '../assets/NewsImg.png';
import PopServ from '../assets/PopServ.png'
import partners from '../assets/partners.png'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'

let active = 1
const itemsPerPage = 3; // Количество карточек на странице
const totalItems = 15; // Общее количество карточек

function Partners() {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // Функция для генерации массива карточек на основе текущей страницы
  const getCurrentPageItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return Array.from({ length: totalItems }, (_, index) => index).slice(startIndex, endIndex);
  };

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Функция для генерации элементов пагинации
  const renderPaginationItems = () => {
    const items = [];
    for (let number = 1; number <= totalPages; number++) {
      items.push(
        <Pagination.Item style={{
          width: '30px',
          height: '30px',
          borderRadius: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          margin: '0 5px',
          backgroundColor: active === number ? 'blue' : 'lightgray',
          color: 'white',
        }} key={number} active={number === currentPage} onClick={() => handlePageChange(number)}>
          {number}
        </Pagination.Item>
      );
    }
    return items;
  };

  return (
    <div>
    <Container className='justify-content-center'>
      <Row  xs ={1} md={3} lg={3} сlassName="mt-5 justify-content-center">
        {getCurrentPageItems().map((itemIndex) => (
          <Col key={itemIndex} className='g-5'>
            <Card className='card-Popular-Services' style={{border:'none'}}>
              <Card.Img variant="top" src={partners} style={{marginBottom:'-10px'}} />
              <Card.Body>
                <Card.Title className='caption mb-3 mt-3' style={{textAlign:'center', marginBottom:'4px'}}> Наименование{itemIndex + 1}</Card.Title>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination>
        <Pagination.Prev style={{transform:'translateX(60px) translateY(-225px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
            <div className='justify-content center' style={{display:'flex',transform:'translateX(625px) translateY(50px)' }}>
            {renderPaginationItems()}
            </div>
        <Pagination.Next style={{transform:'translateX(1200px) translateY(-225px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
      </Pagination>
    </div>
  );
}

export default Partners;
