import React, { useState } from 'react';
import { Pagination, Card, Container } from 'react-bootstrap';
import PopServ from '../assets/PopServ.png'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'

let active = 1
const itemsPerPage = 3; // Количество карточек на странице
const totalItems = 15; // Общее количество карточек

function PopularServices() {
  const [currentPage, setCurrentPage] = useState(1);
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // Функция для генерации массива карточек на основе текущей страницы
  const getCurrentPageItems = () => {
    const startIndex = (currentPage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    return Array.from({ length: totalItems }, (_, index) => index).slice(startIndex, endIndex);
  };

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setCurrentPage(pageNumber);
  };

  // Функция для генерации элементов пагинации
  const renderPaginationItems = () => {
    const items = [];
    for (let number = 1; number <= totalPages; number++) {
      items.push(
        <Pagination.Item style={{
          width: '30px',
          height: '30px',
          borderRadius: '50%',
          display: 'flex',
          justifyContent: 'center',
          alignItems: 'center',
          margin: '0 5px',
          backgroundColor: active === number ? 'blue' : 'lightgray',
          color: 'white',
        }} key={number} active={number === currentPage} onClick={() => handlePageChange(number)}>
          {number}
        </Pagination.Item>
      );
    }
    return items;
  };

  return (
    <div>
    <Container className='block-Standart' style={{overflowX:'hidden'}}>
      <Row  xs ={1} сlassName="mt-5 justify-content-center">
        {getCurrentPageItems().map((itemIndex) => (
          <Col key={itemIndex} className='col-md-4 col-lg-4 g-5 block-Standart'>
            <Card className='card-Popular-Services' style={{border:'none'}}>
              <Card.Img variant="top" src={PopServ} width='50%' style={{marginBottom:'-10px'}} />
              <Card.Body>
                <Card.Title className='caption mb-3 mt-3' style={{textAlign:'center', marginBottom:'4px'}}> Название услуги {itemIndex + 1}</Card.Title>
                    <div className='div-Card-2' style={{display:'flex', justifyContent: 'center'}}/>
                <Card.Text className='body-Regular mt-3' style={{color:'white', display:'flex', alignItems:'center', justifyContent:'center', flexGrow:'0'}}>
                    Lorem ipsum dolor sit amet, consectetur adipiscing elit, sed do eiusmod tempor incididunt ut labore et dolore magna aliqua.                   
                </Card.Text>
                <div style={{display:'flex'}}>
                    <Button className='button-2 ms-auto' style={{border:'none'}}>
                        Подробнее
                    </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination className='block-Standart'>
        <Pagination.Prev style={{transform:'translateX(200px) translateY(-225px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
            <div className='block-Standart mt-5'>
            {renderPaginationItems()}
            </div>
        <Pagination.Next style={{transform:'translateX(100px) translateY(-225px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
      </Pagination>
    </div>
  );
}

export default PopularServices;
