import React, { useEffect, useContext } from 'react';
import { Container, Row, Col, Card, Button, Pagination } from 'react-bootstrap';
import { Context } from '../index';
import { observer } from 'mobx-react-lite';
import { fetchDocument } from '../http/documentsAPI';
import {Docs} from '../assets/Docs.svg'

const Documents = observer(() => {
  const { documents } = useContext(Context);

  useEffect(() => {
    fetchDocument(documents.page, documents.limit).then(data => {
      documents.setDocs(data.rows);
      documents.setTotalCount(data.count);
    });
  }, [documents.page, documents.limit]);

  const totalPages = Math.ceil(documents.totalCount / documents.limit);

  const handlePageChange = (pageNumber) => {
    documents.setPage(pageNumber);
  };

  return (
    <div>
    <Container className='block-Standart mt-4'>
      <Row  xs ={1} md={1} lg={3} сlassName="mt-5 block-Standart">
        {documents.docs.map((docsItem, index) => (
          <Col key={index} className='g-4'>
            <Card className='card-Docs' style={{display:'flex', border:'none'}}>
              <Card.Img 
                variant='left' 
                src={Docs} 
                alt='Документ' 
                style={{paddingBottom:'15px', paddingTop:'15px', paddingLeft:'20px'}}/>
              <Card.Body style={{maxWidth:'100%'}}>
                <div className='body-Bold' style={{color:'white', maxWidth:'100'}}> {docsItem.name} </div>
                <div className='body-Small-Text' style={{color:'white', maxWidth:'100%'}}> {docsItem.text} </div>
              </Card.Body>
            </Card>
          </Col>
        ))}
      </Row>
      </Container>
      <Pagination className='block-Standart mt-2'>
        <Pagination.Prev style={{transform:'translateX(-560px) translateY(-85px)'}} disabled={currentPage === 1} onClick={() => handlePageChange(currentPage - 1)} />
            <div className='block-Standart mt-4'>
            {renderPaginationItems()}
            </div>
        <Pagination.Next style={{transform:'translateX(560px) translateY(-85px)'}} disabled={currentPage === totalPages} onClick={() => handlePageChange(currentPage + 1)} />
      </Pagination>
    </div>
  );
})

export default Documents;
