import React, { useState } from 'react';
import { Pagination, Card } from 'react-bootstrap';

const NewsCards = () => {
  const [activePage, setActivePage] = useState(1);
  const itemsPerPage = 10; // Количество элементов на странице
  const totalItems = 100; // Общее количество элементов
  const totalPages = Math.ceil(totalItems / itemsPerPage);

  // Функция для обработки изменения страницы
  const handlePageChange = (pageNumber) => {
    setActivePage(pageNumber);
    // Здесь можно выполнить дополнительные действия при смене страницы
  };

  // Функция для генерации карточек
  const renderCards = () => {
    const startIndex = (activePage - 1) * itemsPerPage;
    const endIndex = startIndex + itemsPerPage;
    const cards = [];

    for (let i = startIndex; i < endIndex && i < totalItems; i++) {
      cards.push(
        <Card key={i} style={{ width: '18rem', marginBottom: '1rem' }}>
          <Card.Body>
            <Card.Title>Card Title {i + 1}</Card.Title>
            <Card.Text>
              Some quick example text to build on the card title and make up the bulk of
              the card's content.
            </Card.Text>
          </Card.Body>
        </Card>
      );
    }

    return cards;
  };

  return (
    <div>
      <h1>My Component</h1>
      <div>{renderCards()}</div>
      <Pagination>
        <Pagination.Prev
          disabled={activePage === 1}
          onClick={() => handlePageChange(activePage - 1)}
        />
        {[...Array(totalPages)].map((_, i) => (
          <Pagination.Item
            key={i}
            active={i + 1 === activePage}
            onClick={() => handlePageChange(i + 1)}
          >
            {i + 1}
          </Pagination.Item>
        ))}
        <Pagination.Next
          disabled={activePage === totalPages}
          onClick={() => handlePageChange(activePage + 1)}
        />
      </Pagination>
    </div>
  );
};

export default NewsCards;
