import React, { useState, useContext, useEffect } from 'react';
import { Context } from '..';
import { Pagination, Card, CardGroup, Container } from 'react-bootstrap';
import Department from '../assets/Department.png'
import Col from 'react-bootstrap/Col';
import Row from 'react-bootstrap/Row';
import Button from 'react-bootstrap/Button';
import '../styles/utility-styles.css';
import '../styles/text-styles.css'
import '../styles/media.css'
import { observer } from 'mobx-react-lite';
import { fetchDepartments } from '../http/departmentsAPI';

const Departments = observer(() => {
  const { departments } = useContext(Context);

  useEffect(() => {
    fetchDepartments().then(data => {
      departments.setDeps(data.rows);
    });
  }, [departments]);

  let slicedDeps = departments.deps.slice(3)

  return (
    <div>
    <Container>
      <Row xs={1} md={3} lg={3} сlassName="mt-5">
      {departments.deps.map((departmentsItem, index) => (
          <Col key={index} className='g-5'>
            <Card 
              className='card-News' 
              style={{backgroundColor:'#F4F5F6', borderColor:'#3D9378', borderWidth:'0.5px'}}>
              <Card.Img 
                variant="top" 
                src={process.env.REACT_APP_API_URL + departmentsItem.image}
                style={{marginBottom:'-10px'}} />
              <Card.Body>
                <Card.Title 
                  className='caption-Gradient' 
                  style={{textAlign:'center', marginBottom:'4px'}}>
                    {departmentsItem.name} 
                </Card.Title>
                <div className='div-Card mt-2 mb-2' style={{display:'flex', justifyContent: 'center'}}/>
                <Card.Text className='body-Small-Text' style={{color:'#3D9378'}}>
                    {departmentsItem.text}
                </Card.Text>
                <div style={{justifyContent:'center', display:'flex'}}>
                <Button className='button-3' style={{border:'none'}}>
                    Подробнее
                </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
      ))}
     </Row>
     <Row xs={1} md={2} lg={2} сlassName="mt-5">
      {slicedDeps.map((departmentsItem, index) => (
          <Col key={index} className='g-5'>
            <Card 
              className='card-News' 
              style={{backgroundColor:'#F4F5F6', borderColor:'#3D9378', borderWidth:'0.5px'}}>
              <Card.Img 
                variant="top" 
                src={process.env.REACT_APP_API_URL + departmentsItem.image}
                style={{marginBottom:'-10px'}} />
              <Card.Body>
                <Card.Title 
                  className='caption-Gradient' 
                  style={{textAlign:'center', marginBottom:'4px'}}>
                    {departmentsItem.name} 
                </Card.Title>
                <div className='div-Card mt-2 mb-2' style={{display:'flex', justifyContent: 'center'}}/>
                <Card.Text className='body-Small-Text' style={{color:'#3D9378'}}>
                    {departmentsItem.text}
                </Card.Text>
                <div style={{justifyContent:'center', display:'flex'}}>
                <Button className='button-3' style={{border:'none'}}>
                    Подробнее
                </Button>
                </div>
              </Card.Body>
            </Card>
          </Col>
      ))}
     </Row>
      </Container>
     
    </div>
  );
})

export default Departments;
